mapper.update:
UPDATE zdh2_cbklist 
SET
picturepath = #{picturepath}
WHERE cbkid =#{cbkid}

mapper.select:
select pictureid,picturename,picturepath,cbkid
from zdh2_cbkpicture u 
where cbkid = #{cbkid}

select pictureid,picturename,picturepath,pictureurl,point from (select a.pictureid,a.picturename,a.picturepath,a.pictureurl,a.missionid,b.point from zdh2_picture a left join zdh2_result b on a.picturename = b.picturename where a.missionid = #{missionid}
and (b.point >= #{pointmin} and b.point <= #{pointmax} or b.point is null)
and (b.point >= #{pointmin} and b.point <= #{pointmax})
and (b.point is null)
) c order by point DESC

select pictureid,picturepath,pictureurl
from zdh2_picture u
where pictureid = #{pictureid}

mapper.update:
UPDATE zdh2_mission 
SET
style = #{style}
WHERE missionid =#{missionid}

mapper.delete:
delete from zdh2_mission 
WHERE missionid =#{missionid};
delete from zdh2_picture
WHERE missionid =#{missionid};
delete from zdh2_result
WHERE missionid =#{missionid};
